import { AuthContext } from "context/AuthContext";
import { db } from "database/firebase";
import { collection, getDocs, orderBy, query, where } from "firebase/firestore";
import React, { useContext, useEffect, useState } from "react";

// react-bootstrap components
import {
  Card,
  Table,
  Container,
  Row,
  Col,
  Form,
  Dropdown,
  Button,
} from "react-bootstrap";

function ShowExpenses() {
  const [expenses, setExpenses] = useState([]);
  const { currentUser } = useContext(AuthContext);
  const [editedExpense, setEditedExpense] = useState();

  useEffect(() => {
    try {
      const q = query(
        collection(db, "Expenses"),
        where("user_id", "==", currentUser.uid),
        orderBy("created_at", "desc")
      );

      getDocs(q).then((querySnapshot) => {
        setExpenses([]);

        querySnapshot.forEach((docSnapshot) => {
          setExpenses((oldVal) => [...oldVal, docSnapshot.data()]);
        });
      });
    } catch (error) {
      console.log(error);
    }
  }, [currentUser]);

  return (
    <>
      <Container fluid>
        <Row>
          <Col md="12">
            <Card className="strpied-tabled-with-hover">
              <Card.Header>
                <Card.Title as="h4">List of Expenses</Card.Title>
                <p className="card-category">
                  See detailed information about what you have spent
                </p>
              </Card.Header>
              <Card.Body className="table-full-width table-responsive px-0">
                <Table className="table-hover table-striped">
                  <thead>
                    <tr>
                      <th className="border-0">ID</th>
                      <th className="border-0">Expense Name</th>
                      <th className="border-0">Category</th>
                      <th className="border-0">Total Price</th>
                      <th className="border-0">Date</th>
                      <th className="border-0">Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {expenses.map((expense, index) => {
                      const [datePart, timePart] =
                        expense.created_at.split(" "); // split date and time parts
                      const [year, month, day] = datePart
                        .split("-")
                        .map(Number); // split date part into year, month, and day
                      const [hours, minutes, seconds] = timePart
                        .split(":")
                        .map(Number); // split time part into hours, minutes, and seconds

                      const monthNames = [
                        "Jan",
                        "Feb",
                        "Mar",
                        "Apr",
                        "May",
                        "Jun",
                        "Jul",
                        "Aug",
                        "Sep",
                        "Oct",
                        "Nov",
                        "Dec",
                      ];
                      const monthName = monthNames[month - 1];

                      const formattedDate = `${monthName} ${day}, ${year} at ${hours}:${minutes}:${seconds}`;
                      return (
                        <>
                          <tr key={index}>
                            <td>{index + 1}</td>
                            <td>{expense.expenseName}</td>
                            <td>{expense.expenseCategory}</td>
                            <td>$ {expense.expenseTotalPrice}</td>
                            <td>{formattedDate}</td>
                            <td>
                              <button className="btn btn-sm btn-warning mr-1">
                                Edit
                              </button>
                              <button className="btn btn-sm btn-danger">
                                Delete
                              </button>
                            </td>
                          </tr>
                          <tr>
                          <td colSpan={6}>
                          <Col className="w-100" md='12'>
                            <Card>
                              <Card.Body>
                                <Form style={{ width: "100%", height: "15rem" }}>
                                  <Form.Group>
                                    <label>Expense Name</label>
                                    <Form.Control
                                      type="text"
                                      // value={expenseName}
                                      // onChange={(e) =>
                                      //   setExpenseName(e.target.value)
                                      // }
                                      required
                                    ></Form.Control>
                                  </Form.Group>
                                  <Dropdown className="d-flex form-select flex-column my-3 h-25">
                                    <label htmlFor="dropdown">Category</label>
                                    <select
                                      id="dropdown"
                                      className="form-control"
                                      // onChange={handleChange}
                                    >
                                      <option value="Fruits">Fruits</option>
                                      <option value="Vegetables">
                                        Vegetables
                                      </option>
                                      <option value="Fun">Fun</option>
                                      <option value="Luxury">Luxury</option>
                                      <option value="Other">Other</option>
                                    </select>
                                  </Dropdown>
                                  <Form.Group>
                                    <label>Expense Total Price</label>
                                    <Form.Control
                                      type="number"
                                      // value={expenseName}
                                      // onChange={(e) =>
                                      //   setExpenseName(e.target.value)
                                      // }
                                      required
                                    ></Form.Control>
                                  </Form.Group>
                                </Form>
                              </Card.Body>
                            </Card>
                          </Col>
                          </td>
                          </tr>
                        </>
                      );
                    })}
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </>
  );
}

export default ShowExpenses;
