import { AuthContext } from "context/AuthContext";
import { db } from "database/firebase";
import { collection, getDocs, orderBy, query, where } from "firebase/firestore";
import React, { useContext, useEffect, useState } from "react";

// react-bootstrap components
import { Card, Table, Container, Row, Col } from "react-bootstrap";

function TableList() {
  const [expenses, setExpenses] = useState([]);
  const { currentUser } = useContext(AuthContext);

  useEffect(() => {

    try {
      const q = query(
        collection(db, "Expenses"),
        where("user_id", "==", currentUser.uid),
        orderBy('created_at', "desc")
      );

      getDocs(q).then((querySnapshot) => {
        setExpenses([]);

        querySnapshot.forEach((docSnapshot) => {
          setExpenses((oldVal) => [...oldVal, docSnapshot.data()]);
        });

      });
    } catch (error) {
      console.log(error);
    }
  }, [currentUser])

  return (
    <>
      <Container fluid>
        <Row>
          <Col md="12">
            <Card className="strpied-tabled-with-hover">
              <Card.Header>
                <Card.Title as="h4">List of Sub Users</Card.Title>
                <p className="card-category">
                  Check Data about all the subusers
                </p>
              </Card.Header>
              <Card.Body className="table-full-width table-responsive px-0">
                <Table className="table-hover table-striped">
                  <thead>
                    <tr>
                      <th className="border-0">ID</th>
                      <th className="border-0">Expense Name</th>
                      <th className="border-0">Category</th>
                      <th className="border-0">Total Price</th>
                      <th className="border-0">Date</th>
                    </tr>
                  </thead>
                  <tbody>
                    {expenses.map((expense, index) => {
                      return (
                        <tr key={index}>
                          <td>{index + 1}</td>
                          <td>{expense.expenseName}</td>
                          <td>{expense.expenseCategory}</td>
                          <td>$ {expense.expenseTotalPrice}</td>
                          <td>{expense.created_at.to}</td>
                        </tr>
                      );
                    })}
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </>
  );
}

export default TableList;
